  const convertReferencesToLinks = () => {
    const walker = document.createTreeWalker(document.body, NodeFilter.SHOW_TEXT, null, false);
    let node;
  
    while ((node = walker.nextNode())) {
      const parent = node.parentNode;
  
      // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ —Ä–æ–¥–∏—Ç–µ–ª—å—Å–∫–∏–π —ç–ª–µ–º–µ–Ω—Ç –¥–æ–ø—É—Å–∫–∞–µ—Ç –≤—Å—Ç–∞–≤–∫—É HTML
      if (parent && parent.nodeName !== "SCRIPT" && parent.nodeName !== "STYLE") {
        const text = node.nodeValue;
  
        // –ó–∞–º–µ–Ω—è–µ–º [—á–∏—Å–ª–æ] –Ω–∞ –∫–ª–∏–∫–∞–±–µ–ª—å–Ω—ã–µ —Å—Å—ã–ª–∫–∏
        const updatedText = text.replace(/\[(\d+)\]/g, (match, number) => {
          const url = references[number];
          if (url) {
            return `<a href="${url}" target="_blank">${match}</a>`;
          }
          return match; // –ï—Å–ª–∏ —Å—Å—ã–ª–∫–∏ –Ω–µ—Ç, –æ—Å—Ç–∞–≤–∏—Ç—å —Ç–µ–∫—Å—Ç –∫–∞–∫ –µ—Å—Ç—å
        });
  
        // –ï—Å–ª–∏ —Ç–µ–∫—Å—Ç –∏–∑–º–µ–Ω–∏–ª—Å—è, –∑–∞–º–µ–Ω—è–µ–º –µ–≥–æ HTML
        if (updatedText !== text) {
          const span = document.createElement("span");
          span.innerHTML = updatedText;
          parent.replaceChild(span, node);
        }
      }
    }
  };
  

    
